mapper.insert:
INSERT INTO tbl_channel (id, version, channelCode, channelName, sort,cacheLimit, isDefault, status, createTime, updateTime ) VALUES
( #{item.id,jdbcType=VARCHAR}, #{item.version,jdbcType=VARCHAR}, #{item.channelCode,jdbcType=VARCHAR}, #{item.channelName,jdbcType=VARCHAR}, #{item.sort,jdbcType=VARCHAR}, #{item.cacheLimit,jdbcType=VARCHAR}, #{item.isDefault,jdbcType=VARCHAR}, #{item.status,jdbcType=VARCHAR}, NOW(), #{item.updateTime,jdbcType=VARCHAR}
)

mapper.select:
        SELECT id as ID, 
            version, channelCode, channelName, sort,cacheLimit, isDefault, 
            status, createTime, updateTime
        FROM tbl_channel
		1=1
            and version = #{version} 
            and channelCode = #{channelCode} 
            and channelName = #{channelName} 
            and isDefault = #{isDefault} 
            and status = #{status} 
            and createTime = #{createTime} 
            and updateTime = #{updateTime} 
		
		order by sort asc;

        SELECT channelCode, channelName,cacheLimit,sort
        FROM tbl_channel
		1=1
            and channelCode = #{channelCode} 
            and channelName = #{channelName} 
            and isDefault = #{isDefault} 
            and status = #{status} 
		
		order by sort asc;

mapper.update:
UPDATE tbl_channel
version = #{version}, channelCode = #{channelCode}, channelName = #{channelName}, sort = #{sort}, isDefault = #{isDefault}, status = #{status}, createTime = #{createTime}, updateTime = #{updateTime},
WHERE id = #{id}

update.foreach:
UPDATE tbl_channel
version = #{item.version}, channelCode = #{item.channelCode}, channelName = #{item.channelName}, sort = #{item.sort}, isDefault = #{item.isDefault}, status = #{item.status}, createTime = #{item.createTime}, updateTime = #{item.updateTime},
WHERE id = #{item.id}
