mapper.insert:
INSERT INTO tbl_user_attention (id, userId, attentionId, type, status, createTime, updateTime ) VALUES
( #{item.id,jdbcType=VARCHAR}, #{item.userId,jdbcType=VARCHAR}, #{item.attentionId,jdbcType=VARCHAR}, #{item.type,jdbcType=VARCHAR}, #{item.status,jdbcType=VARCHAR}, NOW(), #{item.updateTime,jdbcType=VARCHAR}
)

mapper.select:
        SELECT id as ID, 
            userId, attentionId, type, status, createTime, 
            updateTime
        FROM tbl_user_attention
		1=1
            and userId = #{userId} 
            and attentionId = #{attentionId} 
            and type = #{type} 
            and status = #{status} 
            and createTime = #{createTime} 
            and updateTime = #{updateTime}

mapper.update:
UPDATE tbl_user_attention
userId = #{userId}, attentionId = #{attentionId}, type = #{type}, status = #{status}, createTime = #{createTime}, updateTime = #{updateTime},
WHERE id = #{id}

update.foreach:
UPDATE tbl_user_attention
userId = #{item.userId}, attentionId = #{item.attentionId}, type = #{item.type}, status = #{item.status}, createTime = #{item.createTime}, updateTime = #{item.updateTime},
WHERE id = #{item.id}

mapper.update:
update tbl_user_attention
set status = 1
where userId = #{userId,jdbcType=VARCHAR} and attentionId = #{attentionId,jdbcType=VARCHAR}
